import React, { useState } from "react";
import { useSelector, useDispatch } from "react-redux";
import { useNavigate, Link } from "react-router-dom";
import { login, logout } from "../redux/actions";

function TopBar() {
  const user = useSelector((state) => state.user);
  const dispatch = useDispatch();
  const navigate = useNavigate();
  const [showUploadModal, setShowUploadModal] = useState(false);
  const [uploadFile, setUploadFile] = useState(null);
  const [uploadPreview, setUploadPreview] = useState(null);
  const [uploadDescription, setUploadDescription] = useState("");
  const [isUploading, setIsUploading] = useState(false);
  const [showEditProfileModal, setShowEditProfileModal] = useState(false);
  const [editProfileData, setEditProfileData] = useState({
    first_name: user.firstName || "",
    last_name: "",
    location: "",
    description: "",
    occupation: "",
  });
  const [editProfileError, setEditProfileError] = useState("");

  const handleLogout = async () => {
    try {
      const response = await fetch("http://localhost:5000/api/admin/logout", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        credentials: "include",
      });

      if (!response.ok) {
        throw new Error("Logout failed");
      }

      dispatch(logout());
      navigate("/login");
    } catch (error) {
      console.error("Logout failed:", error);
    }
  };

  const handleAddPhoto = () => {
    setUploadFile(null);
    setUploadPreview(null);
    setUploadDescription("");
    setShowUploadModal(true);
  };

  const handleFileSelect = (e) => {
    const file = e.target.files[0];
    if (file) {
      setUploadFile(file);

      const reader = new FileReader();
      reader.onloadend = () => {
        setUploadPreview(reader.result);
      };
      reader.readAsDataURL(file);
    }
  };

  const handleUploadSubmit = async () => {
    if (!uploadFile) {
      alert("Please select a file to upload");
      return;
    }

    setIsUploading(true);
    const formData = new FormData();
    formData.append("file", uploadFile);
    formData.append("description", uploadDescription);

    try {
      const response = await fetch("http://localhost:5000/api/photos/new", {
        method: "POST",
        body: formData,
        credentials: "include",
      });

      if (!response.ok) {
        throw new Error("Upload failed");
      }

      setIsUploading(false);
      setShowUploadModal(false);
      alert("Photo uploaded successfully!");
      navigate(`/users/${user.userId}/photos`);
    } catch (error) {
      console.error("Photo upload failed:", error);
      setIsUploading(false);
      alert("Failed to upload photo. Please try again.");
    }
  };

  const handleEditProfileChange = (e) => {
    setEditProfileData({ ...editProfileData, [e.target.name]: e.target.value });
  };

  const handleEditProfileSubmit = async () => {
    setEditProfileError("");
    try {
      const response = await fetch("http://localhost:5000/api/user", {
        method: "PUT",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(editProfileData),
        credentials: "include",
      });

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.error || "Failed to update profile");
      }

      const updatedUser = await response.json();
      // Update Redux state with new user info
      dispatch(login({
        _id: user.userId,
        first_name: updatedUser.user.first_name,
        login_name: user.loginName,
      }));
      setShowEditProfileModal(false);
      alert("Profile updated successfully!");
    } catch (error) {
      setEditProfileError(error.message || "Failed to update profile");
    }
  };

  const homeUrl = user.isLoggedIn ? `/users/${user.userId}/photos` : "/login";

  return (
    <>
      <nav className="navbar">
        <Link to={homeUrl} className="navbar-brand">
          Photo Sharing App
        </Link>
        <div className="navbar-nav">
          {user.isLoggedIn ? (
            <>
              <div className="nav-item me-3 d-flex align-items-center text-light">
                Hi {user.firstName}
                <button
                  className="btn btn-link text-light text-decoration-none ms-2"
                  onClick={() => setShowEditProfileModal(true)}
                >
                  Edit Profile
                </button>
              </div>
              <button
                className="btn btn-outline-light me-2"
                onClick={handleAddPhoto}
              >
                Add Photo
              </button>
              <button className="btn btn-outline-light" onClick={handleLogout}>
                Logout
              </button>
            </>
          ) : (
            <div className="nav-item text-light">Please Login</div>
          )}
        </div>
      </nav>

      {/* Upload Photo Modal */}
      {showUploadModal && <div className="modal-backdrop"></div>}
      {showUploadModal && (
        <div className="modal">
          <div className="modal-dialog modal-lg">
            <div className="modal-content">
              <div className="modal-header">
                <h5 className="modal-title">Upload New Photo</h5>
                <button
                  type="button"
                  className="btn-close"
                  onClick={() => !isUploading && setShowUploadModal(false)}
                  disabled={isUploading}
                ></button>
              </div>
              <div className="modal-body">
                <div className="row">
                  <div className="col-md-6">
                    <div
                      className="upload-preview-container mb-3 d-flex align-items-center justify-content-center bg-light rounded"
                      style={{ height: "300px", cursor: "pointer" }}
                      onClick={() =>
                        document.getElementById("uploadPhotoInput").click()
                      }
                    >
                      {uploadPreview ? (
                        <img
                          src={uploadPreview}
                          alt="Preview"
                          style={{
                            maxWidth: "100%",
                            maxHeight: "100%",
                            objectFit: "contain",
                          }}
                        />
                      ) : (
                        <div className="text-center p-3">
                          <i
                            className="bi bi-cloud-arrow-up"
                            style={{ fontSize: "3rem" }}
                          ></i>
                          <p className="mt-2">Click to select a photo</p>
                        </div>
                      )}
                    </div>
                    <input
                      type="file"
                      id="uploadPhotoInput"
                      style={{ display: "none" }}
                      accept="image/*"
                      onChange={handleFileSelect}
                    />
                    <button
                      className="btn btn-outline-primary w-100 mb-3"
                      onClick={() =>
                        document.getElementById("uploadPhotoInput").click()
                      }
                    >
                      {uploadFile ? "Change Photo" : "Select Photo"}
                    </button>
                  </div>
                  <div className="col-md-6">
                    <div className="form-group mb-3">
                      <label className="form-label">Description</label>
                      <textarea
                        className="form-control"
                        rows={5}
                        value={uploadDescription}
                        onChange={(e) => setUploadDescription(e.target.value)}
                        placeholder="Add a description for your photo (optional)"
                      ></textarea>
                    </div>
                    <div className="text-muted mb-3">
                      <small>
                        <i className="bi bi-info-circle me-1"></i>
                        Supported formats: JPG, PNG, GIF
                      </small>
                    </div>
                  </div>
                </div>
              </div>
              <div className="modal-footer">
                <button
                  className="btn btn-secondary"
                  onClick={() => setShowUploadModal(false)}
                  disabled={isUploading}
                >
                  Cancel
                </button>
                <button
                  className="btn btn-primary"
                  onClick={handleUploadSubmit}
                  disabled={!uploadFile || isUploading}
                >
                  {isUploading ? (
                    <>
                      <span
                        className="spinner-border spinner-border-sm me-2"
                        role="status"
                        aria-hidden="true"
                      ></span>
                      Uploading...
                    </>
                  ) : (
                    <>Upload Photo</>
                  )}
                </button>
              </div>
            </div>
          </div>
        </div>
      )}

      {/* Edit Profile Modal */}
      {showEditProfileModal && <div className="modal-backdrop"></div>}
      {showEditProfileModal && (
        <div className="modal">
          <div className="modal-dialog">
            <div className="modal-content">
              <div className="modal-header">
                <h5 className="modal-title">Edit Profile</h5>
                <button
                  type="button"
                  className="btn-close"
                  onClick={() => setShowEditProfileModal(false)}
                ></button>
              </div>
              <div className="modal-body">
                {editProfileError && (
                  <div className="alert alert-danger py-2 mb-4">
                    <i className="bi bi-exclamation-circle me-2"></i>
                    {editProfileError}
                  </div>
                )}
                <div className="form-group mb-3">
                  <label className="form-label">First Name</label>
                  <input
                    type="text"
                    className="form-control"
                    name="first_name"
                    value={editProfileData.first_name}
                    onChange={handleEditProfileChange}
                    required
                  />
                </div>
                <div className="form-group mb-3">
                  <label className="form-label''">Last Name</label>
                  <input
                    type="text"
                    className="form-control"
                    name="last_name"
                    value={editProfileData.last_name}
                    onChange={handleEditProfileChange}
                    required
                  />
                </div>
                <div className="form-group mb-3">
                  <label className="form-label">Location</label>
                  <input
                    type="text"
                    className="form-control"
                    name="location"
                    value={editProfileData.location}
                    onChange={handleEditProfileChange}
                  />
                </div>
                <div className="form-group mb-3">
                  <label className="form-label">Occupation</label>
                  <input
                    type="text"
                    className="form-control"
                    name="occupation"
                    value={editProfileData.occupation}
                    onChange={handleEditProfileChange}
                  />
                </div>
                <div className="form-group mb-3">
                  <label className="form-label">Description</label>
                  <textarea
                    className="form-control"
                    name="description"
                    rows={3}
                    value={editProfileData.description}
                    onChange={handleEditProfileChange}
                    placeholder="Tell us about yourself (optional)"
                  ></textarea>
                </div>
              </div>
              <div className="modal-footer">
                <button
                  className="btn btn-secondary"
                  onClick={() => setShowEditProfileModal(false)}
                >
                  Cancel
                </button>
                <button
                  className="btn btn-primary"
                  onClick={handleEditProfileSubmit}
                >
                  Save Changes
                </button>
              </div>
            </div>
          </div>
        </div>
      )}
    </>
  );
}

export default TopBar;
